import binascii

hex_metin = ""
hex_metin = hex_metin[2:]  # "0x" ön eki kaldırılıyor

try:
    metin = bytearray.fromhex(hex_metin).decode('utf-8')
except UnicodeDecodeError:
    try:
        metin = bytearray.fromhex(hex_metin).decode('latin-1')
    except UnicodeDecodeError:
        try:
            metin = bytearray.fromhex(hex_metin).decode('windows-1252')
        except UnicodeDecodeError:
            try:
                metin = bytearray.fromhex(hex_metin).decode('utf-16')
            except UnicodeDecodeError:
                metin = "Metin çözümlenemedi"

print(metin)

